(88,'Stacked , inline forms','<form action=\"\">\r\n	<div class=\"form-group\">\r\n		<label for=\"email\">Email address:</label>\r\n		<input type=\"email\" class=\"form-control\" id=\"email\">\r\n	</div>\r\n<button type=\"submit\" class=\"btn btn-primary\">Submit</button>\r\n</form>\r\n\r\n<h5>Inline form</h5>\r\n\r\n<form class=\"form-inline\" action=\"\">\r\n	<label for=\"email\">Email address:</label>\r\n 	 <input type=\"email\" class=\"form-control\" id=\"email\">\r\n  	<label for=\"pwd\">Password:</label>\r\n  	<input type=\"password\" class=\"form-control\" id=\"pwd\">\r\n  	<div class=\"form-check\">\r\n    	<label class=\"form-check-label\">\r\n      		<input class=\"form-check-input\" type=\"checkbox\"> Remember me\r\n    	</label>\r\n  	</div>\r\n  	<button type=\"submit\" class=\"btn btn-primary\">Submit</button>\r\n</form>\r\n\r\n','forms'),
(89,'form with validation using .was-validated','<form action=\"\" class=\"was-validated\">\r\n  <div class=\"form-group\">\r\n    <label for=\"uname\">Username:</label>\r\n    <input type=\"text\" class=\"form-control\" id=\"uname\" placeholder=\"Enter username\" name=\"uname\" required>\r\n    <div class=\"valid-feedback\">Valid.</div>\r\n    <div class=\"invalid-feedback\">Please fill out this field.</div>\r\n  </div>\r\n  <div class=\"form-group form-check\">\r\n    <label class=\"form-check-label\">\r\n      <input class=\"form-check-input\" type=\"checkbox\" name=\"remember\" required> I agree on blabla.\r\n      <div class=\"valid-feedback\">Valid.</div>\r\n      <div class=\"invalid-feedback\">Check this checkbox to continue.</div>\r\n    </label>\r\n  </div>\r\n  <button type=\"submit\" class=\"btn btn-primary\">Submit</button>\r\n</form>','forms'),
(90,'form with validation using .needs-validation (after submission)','<form action=\"\" class=\"needs-validation\" novalidate>\r\n  <div class=\"form-group\">\r\n    <label for=\"uname\">Username:</label>\r\n    <input type=\"text\" class=\"form-control\" id=\"uname\" placeholder=\"Enter username\" name=\"uname\" required>\r\n    <div class=\"valid-feedback\">Valid.</div>\r\n    <div class=\"invalid-feedback\">Please fill out this field.</div>\r\n  </div>\r\n  <button type=\"submit\" class=\"btn btn-primary\">Submit</button>\r\n</form>\r\n\r\n<script>\r\n// Disable form submissions if there are invalid fields\r\n(function() {\r\n  \'use strict\';\r\n  window.addEventListener(\'load\', function() {\r\n    // Get the forms we want to add validation styles to\r\n    var forms = document.getElementsByClassName(\'needs-validation\');\r\n    // Loop over them and prevent submission\r\n    var validation = Array.prototype.filter.call(forms, function(form) {\r\n      form.addEventListener(\'submit\', function(event) {\r\n        if (form.checkValidity() === false) {\r\n          event.preventDefault();\r\n          event.stopPropagation();\r\n        }\r\n        form.classList.add(\'was-validated\');\r\n      }, false);\r\n    });\r\n  }, false);\r\n})();\r\n</script>','forms'),
(91,'form inputs','<div class=\"form-group\">\r\n  <label for=\"usr\">Name:</label>\r\n  <input type=\"text\" class=\"form-control\" id=\"usr\">\r\n</div>\r\n\r\n<div class=\"form-group\">\r\n  <label for=\"pwd\">Password:</label>\r\n  <input type=\"password\" class=\"form-control\" id=\"pwd\">\r\n</div>\r\n\r\n<div class=\"form-group\">\r\n  <label for=\"comment\">Comment:</label>\r\n  <textarea class=\"form-control\" rows=\"5\" id=\"comment\"></textarea>\r\n</div>\r\n\r\n<div class=\"form-check\">\r\n  <label class=\"form-check-label\">\r\n    <input type=\"checkbox\" class=\"form-check-input\" value=\"\">Option 1 full width\r\n  </label>\r\n</div>\r\n\r\n\r\n<div class=\"form-check-inline\">\r\n  <label class=\"form-check-label\">\r\n    <input type=\"checkbox\" class=\"form-check-input\" value=\"\">Option 1 inline\r\n  </label>\r\n</div>\r\n<div class=\"form-check-inline\">\r\n  <label class=\"form-check-label\">\r\n    <input type=\"checkbox\" class=\"form-check-input\" value=\"\">Option 2 inline\r\n  </label>\r\n</div>\r\n\r\n<div class=\"form-check\">\r\n  <label class=\"form-check-label\">\r\n    <input type=\"radio\" class=\"form-check-input\" name=\"optradio\">Option 1\r\n  </label>\r\n</div>\r\n\r\n\r\n<div class=\"form-group\">\r\n  <label for=\"sel1\">Select list:</label>\r\n  <select class=\"form-control\" id=\"sel1\">\r\n    <option>1</option>\r\n    <option>2</option>\r\n    <option>3</option>\r\n    <option>4</option>\r\n  </select>\r\n</div>\r\n\r\n<input type=\"range\" class=\"form-control-range\">\r\n<input type=\"file\" class=\"form-control-file border\">\r\n\r\n\r\n<p>\r\n  .form-group is used for full width & propper padding..form-check\" to ensure proper margins.\r\n</p>','forms'),
(92,'form sizes form-control form-control-[sm|lg]','<input type=\"text\" class=\"form-control form-control-sm\">\r\n<input type=\"text\" class=\"form-control form-control\">\r\n<input type=\"text\" class=\"form-control form-control-lg\">','forms'),
(93,'form input grouping','\r\n<form>\r\n  <div class=\"input-group mb-3\">\r\n    <div class=\"input-group-prepend\">\r\n      <span class=\"input-group-text\">@</span>\r\n    </div>\r\n    <input type=\"text\" class=\"form-control\" placeholder=\"Username\">\r\n  </div>\r\n\r\n  <div class=\"input-group mb-3\">\r\n    <input type=\"text\" class=\"form-control\" placeholder=\"Your Email\">\r\n    <div class=\"input-group-append\">\r\n      <span class=\"input-group-text\">@example.com</span>\r\n    </div>\r\n  </div>\r\n\r\n  <div class=\"input-group mb-3\">\r\n    <div class=\"input-group-prepend\">\r\n      <span class=\"input-group-text\">One</span>\r\n      <span class=\"input-group-text\">Two</span>\r\n      <span class=\"input-group-text\">Three</span>\r\n    </div>\r\n    <input type=\"text\" class=\"form-control\">\r\n  </div>\r\n\r\n  <div class=\"input-group mb-3\">\r\n  <div class=\"input-group-prepend\">\r\n    <div class=\"input-group-text\">\r\n      <input type=\"checkbox\">\r\n    </div>\r\n  </div>\r\n  <input type=\"text\" class=\"form-control\" placeholder=\"Some text\">\r\n</div>\r\n\r\n<div class=\"input-group mb-3\">\r\n  <input type=\"text\" class=\"form-control\" placeholder=\"Search\">\r\n  <div class=\"input-group-append\">\r\n    <button class=\"btn btn-success\" type=\"submit\">Go</button>\r\n  </div>\r\n</div>\r\n\r\n\r\n</form>\r\n\r\n<p>\r\n  .input-group formaking group,\r\n  .input-group-prepend  for front,\r\n  .input-group-append for behind,\r\n  .input-group-text for style text.\r\n</p>\r\n<p>\r\n  add .input-group-sm,.input-group-lg for sizes inside parent div.\r\n</p>','forms'),
(94,'custom forms','<form>\r\n  <div class=\"custom-control custom-checkbox\">\r\n    <input type=\"checkbox\" class=\"custom-control-input\" id=\"customCheck\" name=\"example1\">\r\n    <label class=\"custom-control-label\" for=\"customCheck\">Check this custom checkbox</label>\r\n  </div>\r\n\r\n  <div class=\"custom-control custom-switch\">\r\n    <input type=\"checkbox\" class=\"custom-control-input\" id=\"switch1\">\r\n    <label class=\"custom-control-label\" for=\"switch1\">Toggle me</label>\r\n  </div>\r\n\r\n  <div class=\"custom-control custom-radio\">\r\n    <input type=\"radio\" class=\"custom-control-input\" id=\"customRadio\" name=\"example1\" value=\"customEx\">\r\n    <label class=\"custom-control-label\" for=\"customRadio\">Custom radio</label>\r\n  </div>\r\n\r\n  <select name=\"cars\" class=\"custom-select-sm\">\r\n    <option selected>Small Custom Select Menu</option>\r\n    <option value=\"volvo\">Volvo</option>\r\n    <option value=\"fiat\">Fiat</option>\r\n    <option value=\"audi\">Audi</option>\r\n  </select>\r\n\r\n  <select name=\"cars\" class=\"custom-select-lg\">\r\n    <option selected>Small Custom Select Menu</option>\r\n    <option value=\"volvo\">Volvo</option>\r\n    <option value=\"fiat\">Fiat</option>\r\n    <option value=\"audi\">Audi</option>\r\n  </select>\r\n\r\n  <label for=\"customRange\">Custom range</label>\r\n  <input type=\"range\" class=\"custom-range\" id=\"customRange\" name=\"points1\">\r\n\r\n  <div class=\"custom-file\">\r\n    <input type=\"file\" class=\"custom-file-input\" id=\"customFile\">\r\n    <label class=\"custom-file-label\" for=\"customFile\">Choose file</label>\r\n  </div>\r\n\r\n  <script>\r\n    // Add the following code if you want the name of the file appear on select\r\n    $(\".custom-file-input\").on(\"change\", function() {\r\n      var fileName = $(this).val().split(\"\\\\\").pop();\r\n      $(this).siblings(\".custom-file-label\").addClass(\"selected\").html(fileName);\r\n    });\r\n  </scrip