(48,'back link check using php','function check_back_link($remote_url, $your_link) {\r\n  $match_pattern = preg_quote(rtrim($your_link, \"/\"), \"/\"); \r\n  $found = false;\r\n  if($handle = @fopen($remote_url, \"r\")){\r\n    while(!feof($handle)){\r\n      $part = fread($handle, 1024);\r\n      if(preg_match(\"/(.*)/\", $part)){\r\n        $found = true;\r\n        break;\r\n      }\r\n    }\r\n    fclose($handle);\r\n  }\r\n  return $found;\r\n}','if(check_back_link(\'http://www.google.com\',\'http://www.hashbangcode.com\')){\r\n  echo \'link found\';\r\n}else{\r\n  echo \'link NOT found\';\r\n};',''),
(49,'age calculator','<?php\r\n\r\necho \'\'.print_r(getAge(1997,5,1),true).\'\';\r\n\r\nfunction getAge($year,$month,$day,$hour=0,$minute=0,$second=0){\r\n $age = mktime($hour,$minute,$second,$month,$day,$year);\r\n $age = time()-$age;\r\n return array(\'years\'=>$age/60/60/24/365,\r\n  \'months\'=>$age/60/60/24/12,\r\n  \'weeks\'=>$age/60/60/24/7,\r\n  \'days\'=>$age/60/60/24,\r\n  \'hours\'=>$age/60/60,\r\n  \'minutes\'=>$age/60,\r\n  \'seconds\'=>$age);\r\n}','',''),
(50,'get location from zip code','function getLatLong($code){\r\n $mapsApiKey = \'your-google-maps-api-key\';\r\n $query = \"http://maps.google.co.uk/maps/geo?q=\".urlencode($code).\"&output=json&key=\".$mapsApiKey;\r\n $data = file_get_contents($query);\r\n // if data returned\r\n if($data){\r\n  // convert into readable format\r\n  $data = json_decode($data);\r\n  $long = $data->Placemark[0]->Point->coordinates[0];\r\n  $lat = $data->Placemark[0]->Point->coordinates[1];\r\n  return array(\'Latitude\'=>$lat,\'Longitude\'=>$long);\r\n }else{\r\n  return false;\r\n }\r\n}','',''),
(51,'ftp login ','$host= \'ftp.example.com\';\r\n$user = \'notarealusername\';\r\n$password = \'notarealpassword\';\r\n$ftpConn = ftp_connect($host);\r\n$login = ftp_login($ftpConn,$user,$password);\r\n// check connection\r\nif ((!$ftpConn) || (!$login)) {\r\n echo \'FTP connection has failed! Attempted to connect to \'. $host. \' for user \'.$user.\'.\';\r\n}else{\r\n echo \'FTP connection was a success.\';\r\n $directory = ftp_nlist($ftpConn,\'\');\r\n echo \'\'.print_r($directory,true).\'\';\r\n}\r\nftp_close($ftpConn);','',''),
