INSERT INTO `bootstrap4cheetsheet` (`id`, `title`, `code`, `category`) VALUES
(1, 'alerts', '<div class=\"alert alert-primary\" role=\"alert\">\r\n    <strong>alert-primary</strong> Primary alert.\r\n</div>\r\n\r\n<div class=\"alert alert-secondary\" role=\"alert\">\r\n    <strong>alert-secondary</strong> alert-secondary\r\n</div>\r\n\r\n<div class=\"alert alert-success\" role=\"alert\">\r\n    <strong>alert-success</strong> alert-success\r\n</div>\r\n\r\n<div class=\"alert alert-info\" role=\"alert\">\r\n    <strong>alert-info</strong> alert-info\r\n</div>\r\n\r\n<div class=\"alert alert-danger\" role=\"alert\">\r\n  <strong>alert-danger</strong>\r\n  <a href=\"#!\" class=\"alert-link\">with this link</a>\r\n  and try submitting again.\r\n</div>\r\n\r\n<div class=\"alert alert-light\" role=\"alert\">\r\n    <strong>alert-light</strong> alert-light\r\n</div>\r\n\r\n<div class=\"alert alert-dark\" role=\"alert\">\r\n	<h4 class=\"alert-heading\">Well done!</h4>\r\n    <strong>alert-dark</strong> with heading\r\n</div>\r\n\r\n<div class=\"alert alert-warning alert-dismissible fade show\" role=\"alert\">\r\n  <button type=\"button\" class=\"close\" data-dismiss=\"alert\" aria-label=\"Close\">\r\n    <span aria-hidden=\"true\">&times;</span>\r\n  </button>\r\n  <strong>alert-warning</strong> with dismissible.\r\n</div>', 'alerts'),
(12, 'three equal columns on all devices', '<div class=\"row\">\r\n    <div class=\"col\" style=\"background-color:lavender;\">.col</div>\r\n    <div class=\"col\" style=\"background-color:orange;\">.col</div>\r\n    <div class=\"col\" style=\"background-color:lavender;\">.col</div>\r\n</div>', 'grid'),
(13, 'responsive columns, this will break <=576 px', '<div class=\"row\">\r\n	<div class=\"col-sm-3\" style=\"background-color:lavender;\">.col-sm-3</div>\r\n	<div class=\"col-sm-3\" style=\"background-color:lavenderblush;\">.col-sm-3</div>\r\n	<div class=\"col-sm-3\" style=\"background-color:lavender;\">.col-sm-3</div>\r\n	<div class=\"col-sm-3\" style=\"background-color:lavenderblush;\">.col-sm-3</div>\r\n</div>', 'grid'),
(14, 'headings h1,h2,h3,h4,h5,h6', '	<h1>h1 Bootstrap heading (2.5rem = 40px)</h1>\r\n	<h2>h2 Bootstrap heading (2rem = 32px)</h2>\r\n	<h3>h3 Bootstrap heading (1.75rem = 28px)</h3>\r\n	<h4>h4 Bootstrap heading (1.5rem = 24px)</h4>\r\n	<h5>h5 Bootstrap heading (1.25rem = 20px)</h5>\r\n	<h6>h6 Bootstrap heading (1rem = 16px)</h6>', 'typography'),
(15, 'display-*', '	<h1 class=\"display-1\">Display 1</h1>\r\n	<h1 class=\"display-2\">Display 2</h1>\r\n	<h1 class=\"display-3\">Display 3</h1>\r\n	<h1 class=\"display-4\">Display 4</h1>', 'typography'),
(16, 'mark tag in bootstrap', '<mark>Use the mark element to highlight text.</mark>', 'typography'),
(17, 'blockquote with any element', '<blockquote class=\"blockquote\">\r\n	<p>The blockquote element is used to present content from another source:</p>\r\n	<footer class=\"blockquote-footer\">From WWF\'s website</footer>\r\n</blockquote>', 'typography'),
(18, 'small tag', '<h1>h1 heading <small>secondary text</small></h1>', 'typography'),
(19, 'abbr tag', '<p>The <abbr title=\"World Health Organization\">WHO</abbr> was founded in 1948.</p>', 'typography'),
(20, 'code tag', '<p>The following HTML elements: <code>span</code>, <code>section</code>, and <code>div</code> defines a section in a document.</p>', 'typography'),
(21, 'kbd tag', '<p>Use <kbd>ctrl + p</kbd> to open the Print dialog box.</p>', 'typography'),
(22, 'pre tag', '<pre>\r\nText in a pre element\r\nis displayed in a fixed-width\r\nfont, and it preserves\r\nboth      spaces and\r\nline breaks.\r\n</pre>', 'typography'),
(23, 'left , right, center , justified , text-nowrap , text-break , text-decoration-none, mono-space ,  te', '<p class=\"text-left\">Left-aligned text.</p>\r\n<p class=\"text-right\">Right-aligned text.</p>      \r\n<p class=\"text-center\">Center-aligned text.</p>\r\n<p class=\"text-justify\">Justified text. Justified text. Justified text. Justified text. Justified text. Justified text.</p>      \r\n<p class=\"text-nowrap\">No wrap text. No wrap text. No wrap text. No wrap text.</p>\r\n<p class=\"text-break\"> on small devices text is divided into multilines</p>\r\n<p>Without .text-break:</p>\r\n<p><a href=\"#\" class=\"text-decoration-none\">A link without underline using text-decoration-none.</a></p>\r\n<p class=\"text-monospace\">Monospaced text.</p>\r\n<p class=\"text-lowercase\">Lowercased text.</p>\r\n<p class=\"text-uppercase\">Uppercased text.</p>      \r\n<p class=\"text-capitalize\">Capitalized text.</p>\r\n<p class=\"text-muted\">Muted text with a <a href=\"#\" class=\"text-reset\">reset link having no link color</a>.</p>', 'typography'),
(24, 'colors in bootstrap', '<span class=\"text-muted\">This text is muted.</span>\r\n<span class=\"text-spanrimary\">This text is imspanortant.</span>\r\n<span class=\"text-success\">This text indicates success.</span>\r\n<span class=\"text-info\">This text respanresents some information.</span>\r\n<span class=\"text-warning\">This text respanresents a warning.</span>\r\n<span class=\"text-danger\">This text respanresents danger.</span>\r\n<span class=\"text-secondary\">Secondary text.</span>\r\n<span class=\"text-dark\">This text is dark grey.</span>\r\n<span class=\"text-body\">Default body color (often black).</span>\r\n<span class=\"text-light\">This text is light grey (on white background).</span>\r\n<span class=\"text-white\">This text is white (on white background).</span>\r\n<span class=\"text-black-50\">Black text with 50% opacity on white background</span>\r\n<span class=\"text-white-50 bg-dark\">White text with 50% opacity on black background</span>\r\n\r\n<span class=\"bg-primary text-white\">This text is important.</span>\r\n<span class=\"bg-success text-white\">This text indicates success.</span>\r\n<span class=\"bg-info text-white\">This text represents some information.</span>\r\n<span class=\"bg-warning text-white\">This text represents a warning.</span>\r\n<span class=\"bg-danger text-white\">This text represents danger.</span>\r\n<span class=\"bg-secondary text-white\">Secondary background color.</span>\r\n<span class=\"bg-dark text-white\">Dark grey background color.</span>\r\n<span class=\"bg-light text-dark\">Light grey background color.</span>', 'colors'),
(25, 'table', '<table class=\"table\">\r\n    <thead>\r\n      <tr>\r\n        <th>Firstname</th>\r\n        <th>Lastname</th>\r\n        <th>Email</th>\r\n      </tr>\r\n    </thead>\r\n    <tbody>\r\n      <tr>\r\n        <td>John</td>\r\n        <td>Doe</td>\r\n        <td>john@example.com</td>\r\n      </tr>\r\n      <tr>\r\n        <td>Mary</td>\r\n        <td>Moe</td>\r\n        <td>mary@example.com</td>\r\n      </tr>\r\n      <tr>\r\n        <td>July</td>\r\n        <td>Dooley</td>\r\n        <td>july@example.com</td>\r\n      </tr>\r\n    </tbody>\r\n</table>\r\n<p>The .table-striped class adds zebra-stripes to a table:</p>\r\n<p>The .table-bordered class adds borders on all sides of the table and cells:</p>\r\n<p>The .table-hover class adds a hover effect (grey background color) on table rows:</p>\r\n<p>The .table-dark class adds a black background to the table:</p>\r\n<p>The .table-borderless class removes borders from the table:</p>\r\n<p>.table-primary , .table-success , .table-danger , .table-info , .table-warning , .table-active , .table-secondary , .table-light , .table-dark , use these in tr</p>\r\n<p>The .thead-dark class adds a black background to table headers. use in thead</p>\r\n<p>The .table-sm class makes the table smaller by cutting cell padding in half:</p>\r\n<p>The .table-responsive class creates a responsive table: an horizontal scrollbar is added to the table on screens that are less than 992px wide (if needed). When viewing on anything larger than 992px wide, there is no difference:</p>\r\n<p>.table-responsive-sm for <576px, .table-responsive-md for< 768px , .table-responsive-lg for < 992px , .table-responsive-xl for < 1200px</p>', 'table'),
(26, 'Images', '<p>The .rounded class adds rounded corners to an image:</p>     \r\n<img src=\"../assets/images/2.jpg\" class=\"rounded\" alt=\"Cinque Terre\" width=\"304\" height=\"236\">\r\n<p>The .rounded-circle class shapes the image to a circle::</p>\r\n<img src=\"../assets/images/2.jpg\" class=\"rounded-circle\" alt=\"Cinque Terre\" width=\"304\" height=\"236\">\r\n\r\n<p>The .img-thumbnail class shapes the image to a thumbnail (bordered):</p>\r\n<img src=\"../assets/images/3.jpg\" class=\"img-thumbnail\" alt=\"Cinque Terre\" width=\"304\" height=\"236\">\r\n<p>align images, float-left, float-right, mx-auto d-block</p>\r\n<img src=\"../assets/images/1.jpg\" class=\"float-left\" width=\"304\" height=\"236\">\r\n<img src=\"../assets/images/2.jpg\" class=\"float-right\" width=\"304\" height=\"236\">\r\n<img src=\"../assets/images/3.jpg\" class=\"mx-auto d-block\" width=\"304\" height=\"236\">\r\n<p>responcive image add img-fluid class to image tag</p>\r\n<img class=\"img-fluid\" src=\"../assets/images/1.jpg\" alt=\"Chania\">', 'Images'),
(27, 'Jumbotron:a big grey box content or information.', '<div class=\"jumbotron\">\r\n	<h1>Bootstrap Tutorial</h1>      \r\n	<p>Bootstrap is the most popular HTML, CSS, and JS framework for developing responsive, mobile-first projects on the web.</p>\r\n</div>', 'jumbotron'),
(28, 'simple buttons.', '<button type=\"button\" class=\"btn\">Basic</button>\r\n<button type=\"button\" class=\"btn btn-primary\">Primary</button>\r\n<button type=\"button\" class=\"btn btn-secondary\">Secondary</button>\r\n<button type=\"button\" class=\"btn btn-success\">Success</button>\r\n<button type=\"button\" class=\"btn btn-info\">Info</button>\r\n<button type=\"button\" class=\"btn btn-warning\">Warning</button>\r\n<button type=\"button\" class=\"btn btn-danger\">Danger</button>\r\n<button type=\"button\" class=\"btn btn-dark\">Dark</button>\r\n<button type=\"button\" class=\"btn btn-light\">Light</button>\r\n<button type=\"button\" class=\"btn btn-link\">Link</button>\r\n\r\n<p>we can use btn btn-* in input, a, and button</p>', 'buttons'),
(29, 'Buttons Outlines', '<button type=\"button\" class=\"btn btn-outline-primary\">Primary</button>\r\n<button type=\"button\" class=\"btn btn-outline-secondary\">Secondary</button>\r\n<button type=\"button\" class=\"btn btn-outline-success\">Success</button>\r\n<button type=\"button\" class=\"btn btn-outline-info\">Info</button>\r\n<button type=\"button\" class=\"btn btn-outline-warning\">Warning</button>\r\n<button type=\"button\" class=\"btn btn-outline-danger\">Danger</button>\r\n<button type=\"button\" class=\"btn btn-outline-dark\">Dark</button>\r\n<button type=\"button\" class=\"btn btn-outline-light text-dark\">Light</button>', 'buttons'),
(30, 'Button size', '<button type=\"button\" class=\"btn btn-primary btn-lg\">Large</button>\r\n<button type=\"button\" class=\"btn btn-primary\">Default</button>\r\n<button type=\"button\" class=\"btn btn-primary btn-sm\">Small</button>', 'buttons'),
(31, 'Block level Button', '<button type=\"button\" class=\"btn btn-primary btn-block\">Full-Width Button</button>', 'buttons'),
(32, 'Active & disabled', '<button type=\"button\" class=\"btn btn-primary active\">Active Primary</button>\r\n<button type=\"button\" class=\"btn btn-primary\" disabled>Disabled Primary</button>\r\n<a href=\"#\" class=\"btn btn-primary disabled\">Disabled Link</a>', 'buttons'),
(33, 'spinning Button', '<button class=\"btn btn-primary\">\r\n  <span class=\"spinner-border spinner-border-sm\"></span>\r\n</button>', 'buttons'),
(34, 'Button group', '<div class=\"btn-group\">\r\n  <button type=\"button\" class=\"btn btn-primary\">Apple</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Samsung</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Sony</button>\r\n</div>', 'buttons'),
(35, 'Button group sizes', '<div class=\"btn-group btn-group-lg\">\r\n  <button type=\"button\" class=\"btn btn-primary\">Apple</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Samsung</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Sony</button>\r\n</div>\r\n\r\n<p>.btn-group-sm for small</p>', 'buttons'),
(36, 'vertical Buttons', '<div class=\"btn-group-vertical\">\r\n  <button type=\"button\" class=\"btn btn-primary\">Apple</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Samsung</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Sony</button>\r\n</div>', 'buttons'),
(37, 'Buttons with dropdown', '<div class=\"btn-group\">\r\n  <button type=\"button\" class=\"btn btn-primary\">Apple</button>\r\n  <button type=\"button\" class=\"btn btn-primary\">Samsung</button>\r\n  <div class=\"btn-group\">\r\n    <button type=\"button\" class=\"btn btn-primary dropdown-toggle\" data-toggle=\"dropdown\">\r\n       Sony\r\n    </button>\r\n    <div class=\"dropdown-menu\">\r\n      <a class=\"dropdown-item\" href=\"#\">Tablet</a>\r\n      <a class=\"dropdown-item\" href=\"#\">Smartphone</a>\r\n    </div>\r\n  </div>\r\n</div>', 'buttons'),
(38, 'Buttons with individual dropdown', '<div class=\"btn-group\">\r\n  <button type=\"button\" class=\"btn btn-primary\">Sony</button>\r\n  <button type=\"button\" class=\"btn btn-primary dropdown-toggle dropdown-toggle-split\" data-toggle=\"dropdown\">\r\n    <span class=\"caret\"></span>\r\n  </button>\r\n  <div class=\"dropdown-menu\">\r\n    <a class=\"dropdown-item\" href=\"#\">Tablet</a>\r\n    <a class=\"dropdown-item\" href=\"#\">Smartphone</a>\r\n  </div>\r\n</div>', 'buttons'),
(39, 'Contextual Badges', '<span class=\"badge badge-primary\">Primary</span>\r\n<span class=\"badge badge-secondary\">Secondary</span>\r\n<span class=\"badge badge-success\">Success</span>\r\n<span class=\"badge badge-danger\">Danger</span>\r\n<span class=\"badge badge-warning\">Warning</span>\r\n<span class=\"badge badge-info\">Info</span>\r\n<span class=\"badge badge-light\">Light</span>\r\n<span class=\"badge badge-dark\">Dark</span>', 'badges'),
(40, 'Pill Badges (more rounded)', '<span class=\"badge badge-pill badge-primary\">Primary</span>\r\n<span class=\"badge badge-pill badge-secondary\">Secondary</span>\r\n<span class=\"badge badge-pill badge-success\">Success</span>\r\n<span class=\"badge badge-pill badge-danger\">Danger</span>\r\n<span class=\"badge badge-pill badge-warning\">Warning</span>\r\n<span class=\"badge badge-pill badge-info\">Info</span>\r\n<span class=\"badge badge-pill badge-light\">Light</span>\r\n<span class=\"badge badge-pill badge-dark\">Dark</span>', 'badges'),
(41, 'Badges with elements', '<button type=\"button\" class=\"btn btn-primary\">\r\n  Messages <span class=\"badge badge-light\">4</span>\r\n</button>', 'badges'),
(42, 'Progress bar', '<div class=\"progress\">\r\n  	<div class=\"progress-bar\" style=\"width:70%\"></div>\r\n</div>', 'progressBar'),
(43, 'Progressbar heights', '<div class=\"progress\" style=\"height:10px\">\r\n	<div class=\"progress-bar\" style=\"width:40%;height:10px\"></div>\r\n</div>\r\n<br>\r\n<div class=\"progress\" style=\"height:20px\">\r\n	<div class=\"progress-bar\" style=\"width:50%;height:20px\"></div>\r\n</div>\r\n<br>\r\n<div class=\"progress\" style=\"height:30px\">\r\n	<div class=\"progress-bar\" style=\"width:60%;height:30px\"></div>\r\n</div>	', 'progressBar'),
(44, 'progressbar label', '<div class=\"progress\">\r\n     <div class=\"progress-bar\" style=\"width:70%\">70%</div>\r\n</div>', 'progressBar'),
(45, 'Progress bar collered', '<div class=\"progress\">\r\n    <div class=\"progress-bar bg-danger\" style=\"width:50%\"></div>\r\n</div>', 'progressBar'),
(46, 'Progress bar .progress-bar-striped', '<div class=\"progress\">\r\n    <div class=\"progress-bar progress-bar-striped\" style=\"width:40%\"></div>\r\n</div>', 'progressBar'),
(47, 'Progress bar .progress-bar-animated', '<div class=\"progress\">\r\n	<div class=\"progress-bar progress-bar-striped progress-bar-animated\" style=\"width:40%\"></div>\r\n</div>', 'progressBar'),
(48, 'Progress bar multi', '<div class=\"progress\">\r\n	<div class=\"progress-bar bg-success\" style=\"width:40%\">\r\n	Free Space\r\n	</div>\r\n	<div class=\"progress-bar bg-warning\" style=\"width:10%\">\r\n	Warning\r\n	</div>\r\n	<div class=\"progress-bar bg-danger\" style=\"width:20%\">\r\n	Danger\r\n	</div>\r\n</div>	', 'progressBar'),
(49, 'spinners', '<div class=\"spinner-border text-muted\"></div>\r\n<div class=\"spinner-border text-primary\"></div>\r\n<div class=\"spinner-border text-success\"></div>\r\n<div class=\"spinner-border text-info\"></div>\r\n<div class=\"spinner-border text-warning\"></div>\r\n<div class=\"spinner-border text-danger\"></div>\r\n<div class=\"spinner-border text-secondary\"></div>\r\n<div class=\"spinner-border text-dark\"></div>\r\n<div class=\"spinner-border text-light\"></div>', 'spinners'),
(50, 'spinners growing', '<div class=\"spinner-grow text-muted\"></div>\r\n<div class=\"spinner-grow text-primary\"></div>\r\n<div class=\"spinner-grow text-success\"></div>\r\n<div class=\"spinner-grow text-info\"></div>\r\n<div class=\"spinner-grow text-warning\"></div>\r\n<div class=\"spinner-grow text-danger\"></div>\r\n<div class=\"spinner-grow text-secondary\"></div>\r\n<div class=\"spinner-grow text-dark\"></div>\r\n<div class=\"spinner-grow text-light\"></div>', 'spinners'),
(51, 'spinners sizes', '<div class=\"spinner-border spinner-border-sm\"></div>\r\n<div class=\"spinner-grow spinner-grow-sm\"></div>\r\n\r\n<p>You can use spinners with any element</p>', 'spinners'),
(52, 'Pagination', '<ul class=\"pagination\">\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Previous</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">1</a></li>\r\n    <li class=\"page-item active\"><a class=\"page-link\" href=\"#\">2</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">3</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Next</a></li>\r\n</ul>', 'pagination'),
(53, 'Pagination .disabled', '<ul class=\"pagination\">\r\n    <li class=\"page-item disabled\"><a class=\"page-link\" href=\"#\">Previous</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">1</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">2</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">3</a></li>\r\n    <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Next</a></li>\r\n</ul>', 'pagination'),
(54, 'Pagination sizes .pagination-lg or pagination-sm', '<ul class=\"pagination pagination-lg\">\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Previous</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">1</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">2</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">3</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Next</a></li>\r\n</ul>\r\n\r\n<ul class=\"pagination pagination-sm\">\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Previous</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">1</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">2</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">3</a></li>\r\n  <li class=\"page-item\"><a class=\"page-link\" href=\"#\">Next</a></li>\r\n</ul>', 'pagination'),
(55, 'pagination alignment .justify-content-center or justify-content-end', '<ul class=\"pagination\">\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">Previous</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">1</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">2</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">Next</a></li>\r\n</ul>\r\n\r\n<ul class=\"pagination justify-content-center\">\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">Previous</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">1</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">2</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">Next</a></li>\r\n</ul>\r\n\r\n<ul class=\"pagination justify-content-end\">\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">Previous</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">1</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">2</a></li>\r\n	<li class=\"page-item\"><a class=\"page-link\" href=\"javascript:void(0);\">Next</a></li>\r\n</ul>', 'pagination'),
(56, 'Breadcrumbs', '<ul class=\"breadcrumb\">\r\n    <li class=\"breadcrumb-item\"><a href=\"#\">Photos</a></li>\r\n    <li class=\"breadcrumb-item\"><a href=\"#\">Summer 2017</a></li>\r\n    <li class=\"breadcrumb-item\"><a href=\"#\">Italy</a></li>\r\n    <li class=\"breadcrumb-item active\">Rome</li>\r\n</ul>', 'breadcrumbs'),
(57, 'List groups', '<ul class=\"list-group\">\r\n	<li class=\"list-group-item active\">First item is active</li>\r\n	<li class=\"list-group-item list-group-item-action\">Second item .list-group-item-action for hover effect</li>\r\n	<li class=\"list-group-item\">Third item</li>\r\n</ul>', 'lists'),
(58, 'List groups .list-group-flush to remove borders', '<ul class=\"list-group list-group-flush\">\r\n    <li class=\"list-group-item\">First item</li>\r\n    <li class=\"list-group-item\">Second item</li>\r\n    <li class=\"list-group-item\">Third item</li>\r\n    <li class=\"list-group-item\">Fourth item</li>\r\n</ul>', 'lists'),
(59, 'List groups horizontal .list-group-horizontal', '<ul class=\"list-group list-group-horizontal\">\r\n    <li class=\"list-group-item\">First item <span class=\"badge badge-primary badge-pill\">12</span> </li>\r\n    <li class=\"list-group-item\">Second item</li>\r\n    <li class=\"list-group-item\">Third item</li>\r\n    <li class=\"list-group-item\">Fourth item</li>\r\n</ul>', 'lists'),
(60, 'basic card', '<div class=\"card\">\r\n	<div class=\"card-header\">Header</div>\r\n	<div class=\"card-body\">Content</div> \r\n	<div class=\"card-footer\">Footer</div>\r\n</div>', 'cards'),
