(194, 'Template tags (retrieve content from your database and files) ||WordPress', '<p><strong>get_header();</strong> //tag to get heade.php</p>\r\n<p><strong>get_footer();</strong> //get footer.php</p>\r\n<p><strong>the_title();</strong> //get title</p>\r\n<p><strong>bloginfo( \'name\' );</strong> //get blog name</p>\r\n<p><strong>#########Get posts ########</strong></p>\r\n<pre class=\"language-php\"><code>if ( have_posts() ) :\r\n    while ( have_posts() ) :\r\n        the_post();\r\n    endwhile;\r\nelse :\r\n    _e( \'Sorry, no posts matched your criteria.\', \'devhub\' );\r\nendif;</code></pre>\r\n<ul>\r\n<li>the_content()</li>\r\n<li>the_excerpt()</li>\r\n<li>next_post()</li>\r\n<li>previous_post(); //we can use all these four tags&nbsp; inside loop</li>\r\n</ul>\r\n<p>get_sidebar()</p>\r\n<p>get_template_part(\'customPhp\')</p>\r\n<p>get_search_form();//search bar</p>\r\n<p>wp_loginout();//</p>\r\n<p>wp_logout_url(); <strong>//log out anchor</strong></p>\r\n<p>wp_login_url(); <strong>// login IN anchor</strong></p>\r\n<p>wp_login_form();<strong> //login form to wordpress</strong></p>\r\n<p>wp_lostpassword_url();//</p>\r\n<p>wp_register();</p>\r\n<p>wp_meta();</p>\r\n<p>bloginfo(\'str\');</p>\r\n<p style=\"padding-left: 40px;\"><strong>//name,description,wpurl,url,admin_email,charset,version,html_type,text_direction,language,stylesheet_url,siteurl,home,etc</strong></p>\r\n<p>get_bloginfo(\'str\',\'raw\');//all above data</p>\r\n<p>get_current_blog_id();//</p>\r\n<p><strong>wp_title();</strong></p>\r\n<p>single_post_title();</p>\r\n<p>post_type_archive_title()</p>\r\n<p>single_cat_title()</p>\r\n<p>single_tag_title()</p>\r\n<p>single_term_title()</p>\r\n<p>single_month_title()</p>\r\n<p>get_archives_link()</p>\r\n<p>wp_get_archives()</p>\r\n<p>calendar_week_mod()</p>\r\n<p>get_calendar()</p>\r\n<p>delete_get_calendar_cache()</p>\r\n<p>allowed_tags()</p>\r\n<p><strong>wp_enqueue_script()</strong></p>\r\n<p>the_author()</p>\r\n<p>get_the_author()</p>\r\n<p>the_author_link()</p>\r\n<p>get_the_author_link()</p>\r\n<p>the_author_meta()</p>\r\n<p>the_author_posts()</p>\r\n<p>the_author_posts_link()</p>\r\n<p>wp_dropdown_users()</p>\r\n<p>wp_list_authors()</p>\r\n<p>get_author_posts_url()</p>\r\n<p>cancel_comment_reply_link()</p>\r\n<p>comment_author()</p>\r\n<p>comment_author_email()</p>\r\n<p>comment_author_email_link()</p>\r\n<p>comment_author_IP()</p>\r\n<p>comment_author_link()</p>\r\n<p>comment_author_rss()</p>\r\n<p>comment_author_url()</p>\r\n<p>comment_author_url_link()</p>\r\n<p>comment_class()</p>\r\n<p>comment_date()</p>\r\n<p>comment_excerpt()</p>\r\n<p>comment_form_title()</p>\r\n<p>comment_form()</p>\r\n<p>comment_ID()</p>\r\n<p>comment_id_fields()</p>\r\n<p>comment_reply_link()</p>\r\n<p>comment_text()</p>\r\n<p>comment_text_rss()</p>\r\n<p>comment_time()</p>\r\n<p>comment_type()</p>\r\n<p>comments_link()</p>\r\n<p>comments_number()</p>\r\n<p>comments_popup_link()</p>\r\n<p>get_avatar()</p>\r\n<p>next_comments_link()</p>\r\n<p>paginate_comments_links()</p>\r\n<p>permalink_comments_rss()</p>\r\n<p>previous_comments_link()</p>\r\n<p>wp_list_comments()</p>\r\n<p>he_permalink()</p>\r\n<p>user_trailingslashit()</p>\r\n<p>permalink_anchor()</p>\r\n<p>get_permalink()</p>\r\n<p>get_post_permalink()</p>\r\n<p>get_page_link()</p>\r\n<p>get_attachment_link()</p>\r\n<p>wp_shortlink_header()</p>\r\n<p>wp_shortlink_wp_head()</p>\r\n<p>edit_bookmark_link()</p>\r\n<p>edit_comment_link()</p>\r\n<p>edit_post_link()</p>\r\n<p>get_edit_post_link()</p>\r\n<p>get_delete_post_link()</p>\r\n<p>edit_tag_link()</p>\r\n<p>get_admin_url()</p>\r\n<p>get_home_url()</p>\r\n<p>get_site_url()</p>\r\n<p>home_url()</p>\r\n<p>site_url()</p>\r\n<p>get_search_link()</p>\r\n<p>get_search_query()</p>\r\n<p>the_feed_link()</p>\r\n<p>&nbsp;</p>\r\n<p>body_class()</p>\r\n<p>next_image_link()</p>\r\n<p>next_post_link()</p>\r\n<p>next_posts_link()</p>\r\n<p>post_class()</p>\r\n<p>post_password_required()</p>\r\n<p>posts_nav_link()</p>\r\n<p>previous_image_link()</p>\r\n<p>previous_post_link()</p>\r\n<p>previous_posts_link()</p>\r\n<p>single_post_title()</p>\r\n<p>the_category()</p>\r\n<p>the_category_rss()</p>\r\n<p>the_content()</p>\r\n<p>the_excerpt()</p>\r\n<p>the_excerpt_rss()</p>\r\n<p>the_ID()</p>\r\n<p>the_meta()</p>\r\n<p>the_tags()</p>\r\n<p>the_title()</p>\r\n<p>get_the_title()</p>\r\n<p>the_title_attribute()</p>\r\n<p>the_title_rss()</p>\r\n<p>wp_link_pages()</p>\r\n<p>get_attachment_link()</p>\r\n<p>wp_get_attachment_link()</p>\r\n<p>the_attachment_link()</p>\r\n<p>the_search_query()</p>\r\n<p>is_attachment()</p>\r\n<p>wp_attachment_is_image()</p>\r\n<p>wp_get_attachment_image()</p>\r\n<p>wp_get_attachment_image_src()</p>\r\n<p>wp_get_attachment_metadata()</p>\r\n<p>get_the_date()</p>\r\n<p>single_month_title()</p>\r\n<p>the_date()</p>\r\n<p>the_date_xml()</p>\r\n<p>the_modified_author()</p>\r\n<p>the_modified_date()</p>\r\n<p>the_modified_time()</p>\r\n<p>the_time()</p>\r\n<p>the_shortlink()</p>\r\n<p>wp_get_shortlink()</p>\r\n<p>has_post_thumbnail()</p>\r\n<p>get_post_thumbnail_id()</p>\r\n<p>the_post_thumbnail()</p>\r\n<p>get_the_post_thumbnail()</p>\r\n<p>wp_nav_menu()</p>\r\n<p>walk_nav_menu_tree()</p>', 14, '2020-03-29 16:39:28'),
(195, 'Loop can display, is home, is admin,is 404,thubnail etc  ||WordPress', '<p>A Loop can display follwing data:</p>\r\n<p>previous_post_link();</p>\r\n<p>next_post_link();</p>\r\n<p>the_category();</p>\r\n<p>the_author();</p>\r\n<p>the_content();</p>\r\n<p>the_excerpt(); //first 55 words of a post&hellip;</p>\r\n<p>the_ID();</p>\r\n<p>the_meta();</p>\r\n<p>the_shortlink();</p>\r\n<p>the_tags();</p>\r\n<p>the_title();</p>\r\n<p>the_title( \'&lt;h2&gt;\', \'&lt;/h2&gt;\' );&nbsp;</p>\r\n<p>the_time();</p>\r\n<p>&nbsp;</p>\r\n<p>is_home() &ndash; Returns true if the current page is the homepage</p>\r\n<p>is_admin() &ndash; Returns true if inside Administration Screen, false otherwise</p>\r\n<p>is_single() &ndash; Returns true if the page is currently displaying a single post</p>\r\n<p>is_page() &ndash; Returns true if the page is currently displaying a single page</p>\r\n<p>is_page_template() &ndash; Can be used to determine if a page is using a specific template, for example: is_page_template(\'about-page.php\')</p>\r\n<p>is_category() &ndash; Returns true if page or post has the specified category, for example: is_category(\'news\')</p>\r\n<p>is_tag() &ndash; Returns true if a page or post has the specified tag</p>\r\n<p>is_author() &ndash; Returns true if inside author&rsquo;s archive page</p>\r\n<p>is_search() &ndash; Returns true if the current page is a search results page</p>\r\n<p>is_404() &ndash; Returns true if the current page does not exist</p>\r\n<p>has_excerpt() &ndash; Returns true if the post or page has an excerpt</p>\r\n<p>the_post_thumbnail();&nbsp;</p>\r\n<p>the_excerpt();</p>\r\n<p>in_category( 3 )</p>', 14, '2020-03-29 16:41:36'),
(196, 'Conditional Tags ,functions ||WordPress', '<pre class=\"language-php\"><code>if ( is_user_logged_in() ):\r\n    echo \'Welcome, registered user!\';\r\nelse:\r\n    echo \'Welcome, visitor!\';\r\nendif;</code></pre>\r\n<p>Function: comments_open()</p>\r\n<p>Function: is_404()</p>\r\n<p>Function: is_admin()</p>\r\n<p>Function: is_admin_bar_showing()</p>\r\n<p>Function: is_archive()</p>\r\n<p>Function: is_attachment()</p>\r\n<p>Function: is_author()</p>\r\n<p>Function: is_category()</p>\r\n<p>Function: is_comments_popup()</p>\r\n<p>Function: is_date()</p>\r\n<p>Function: is_day()</p>\r\n<p>Function: is_feed()</p>\r\n<p>Function: is_front_page()</p>\r\n<p>Function: is_home()</p>\r\n<p>Function: is_local_attachment()</p>\r\n<p>Function: is_main_query</p>\r\n<p>Function: is_multi_author</p>\r\n<p>Function: is_month()</p>\r\n<p>Function: is_new_day()</p>\r\n<p>Function: is_page()</p>\r\n<p>Function: is_page_template()</p>\r\n<p>Function: is_paged()</p>\r\n<p>Function: is_plugin_active()</p>\r\n<p>Function: is_plugin_active_for_network()</p>\r\n<p>Function: is_plugin_inactive()</p>\r\n<p>Function: is_plugin_page()</p>\r\n<p>Function: is_post_type_archive()</p>\r\n<p>Function: is_preview()</p>\r\n<p>Function: is_search()</p>\r\n<p>Function: is_single()</p>\r\n<p>Function: is_singular()</p>\r\n<p>Function: is_sticky()</p>\r\n<p>Function: is_tag()</p>\r\n<p>Function: is_tax()</p>\r\n<p>Function: is_taxonomy_hierarchical()</p>\r\n<p>Function: is_time()</p>\r\n<p>Function: is_trackback()</p>\r\n<p>Function: is_year()</p>\r\n<p>Function: in_category()</p>\r\n<p>Function: in_the_loop()</p>\r\n<p>Function: is_active_sidebar()</p>\r\n<p>Function: is_active_widget()</p>\r\n<p>Function: is_blog_installed()</p>\r\n<p>Function: is_rtl()</p>\r\n<p>Function: is_dynamic_sidebar()</p>\r\n<p>Function: is_user_logged_in()</p>\r\n<p>Function: has_excerpt()</p>\r\n<p>Function: has_post_thumbnail()</p>\r\n<p>Function: has_tag()</p>\r\n<p>Function: pings_open()</p>\r\n<p>Function: email exists()</p>\r\n<p>Function: post_type_exists()</p>\r\n<p>Function: taxonomy_exists()</p>\r\n<p>Function: term_exists()</p>\r\n<p>Function: username exists()</p>\r\n<p>Function: wp_attachment_is_image()</p>\r\n<p>Function: wp_script_is()</p>', 14, '2020-03-29 16:43:25'),
(197, 'widget ,sidebar add ||WordPress', '<ul>\r\n<li>name is human read able name</li>\r\n<li>id is identifier</li>\r\n<li>before_widget,after_widget is to remove extra dots&nbsp;</li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<p><strong>1.add in template files</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;?php if ( is_active_sidebar( \'rightsidebar\' ) ) { ?&gt;\r\n	    &lt;div class=\"sidebar-column\"&gt;&lt;!-- sidebar-column --&gt;\r\n	        &lt;?php dynamic_sidebar( \'rightsidebar\' ) ?&gt;\r\n	    &lt;/div&gt;&lt;!-- sidebar-column --&gt;\r\n&lt;?php } ?&gt;</code></pre>\r\n<p><strong>2.ADD in functions.php</strong></p>\r\n<pre class=\"language-markup\"><code>function initialize_widgets() {\r\n	register_sidebar( [\r\n		\'name\' =&gt; \'Right Sidebar\',\r\n		\'id\'   =&gt; \'rightsidebar\',\r\n		\'before_widget\' =&gt; \'&lt;div class=\"widget-item\"&gt;\',\r\n		\'after_widget\'  =&gt; \'&lt;/div&gt;\'\r\n	] );\r\n}\r\nadd_action( \'widgets_init\', \'initialize_widgets\' );</code></pre>\r\n<p><strong>SYNTAX:</strong></p>\r\n<p>register_sidebar( array(</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; \'name\'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; =&gt; __( \'Primary Sidebar\', \'theme_name\' ),</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; \'id\'&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; =&gt; \'sidebar-1\',</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; \'before_widget\' =&gt; \'&lt;aside id=\"%1$s\" class=\"widget %2$s\"&gt;\',</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; \'after_widget\'&nbsp; =&gt; \'&lt;/aside&gt;\',</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; \'before_title\'&nbsp; =&gt; \'&lt;h3 class=\"widget-title\"&gt;\',</p>\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; \'after_title\'&nbsp; &nbsp;=&gt; \'&lt;/h3&gt;\',</p>\r\n<p>&nbsp; &nbsp; ) );</p>', 14, '2020-03-29 16:46:35'),
(198, 'nav menu ||Wordpress', '<p><strong>1.in header.php</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;nav class=\"navigation-menu\"&gt;\r\n	wp_nav_menu( array( \r\n    		\'theme_location\' =&gt; \'my-custom-menu\', //\r\n		 \'container_class\' =&gt; \'custom-menu-class\' ) //menus are wrapped in this class\r\n	); \r\n&lt;/nav&gt;</code></pre>\r\n<p><strong>2.add in functions.php</strong></p>\r\n<pre class=\"language-php\"><code>function wpb_custom_new_menu() {\r\n  register_nav_menus(\r\n    array(\r\n      \'my-custom-menu\' =&gt; __( \'My Custom Menu\' ),\r\n      \'extra-menu\' =&gt; __( \'Extra Menu\' ) //more than one [optional]\r\n    )\r\n  );\r\n}\r\nadd_action( \'init\', \'wpb_custom_new_menu\' );</code></pre>\r\n<p>3.change style.css</p>\r\n<p><strong>4.Menues are created in Theme apperence</strong></p>', 14, '2020-03-29 16:48:16'),
(199, 'include template parts ie parts/home_counter.php inside index.php ||WordPress', '<p>if You want to include <strong>theme/parts/home_counter.php.</strong>This will be used for feature items etc&nbsp;</p>\r\n<p><strong>###index.php</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;?php\r\nget_template_part(\"parts/home\",\"counter\"); //for home_counter.php\r\nget_template_part(\"parts/home\"); //for home\r\necho get_template_directory_uri().\"/assets/abc.jpg\";\r\n?&gt;\r\n\r\n</code></pre>', 14, '2020-03-29 16:51:22'),
(200, 'Add scripts or stylesheets ||WordPress', '<p><strong>1.Add in head tag</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;?php wp_head();?&gt;</code></pre>\r\n<p><strong>2.functions.php</strong></p>\r\n<pre class=\"language-markup\"><code>function custom_theme_assets() {\r\n	wp_enqueue_style( \'style\', get_stylesheet_uri() ); //get style.css\r\n	wp_enqueue_script(\'myscript\',get_template_directory_uri().\"/mysc.js\");\r\n}\r\nadd_action( \'wp_enqueue_scripts\', \'custom_theme_assets\' );</code></pre>\r\n<p>wp_footer();// contains all footer scripts and styles</p>', 14, '2020-03-29 16:53:42'),
(201, 'Theme Supports ,pagination,custom sizes of images, featured image,logo,title tag ||WordPress', '<p><strong>1.LOGO</strong></p>\r\n<p style=\"padding-left: 40px;\">add_theme_support( \'custom-logo\'); <strong>//functions.php</strong></p>\r\n<p style=\"padding-left: 40px;\">&lt;?php the_custom_logo()?&gt; <strong>//header.php to display logo</strong></p>\r\n<p><strong>2.No need of title tag</strong></p>\r\n<p><span style=\"color: #e03e2d;\">&lt;title&gt;</span></p>\r\n<p><span style=\"color: #e03e2d;\">&nbsp; &nbsp;&lt;?php echo get_the_title();?&gt;</span></p>\r\n<p><span style=\"color: #e03e2d;\">&lt;/title&gt;</span></p>\r\n<p>just use instead of above code</p>\r\n<p><span style=\"color: #2dc26b;\">add_theme_support( \'title-tag\'); //in functions.php</span></p>\r\n<p><strong>3. Add Feature image when new post written</strong></p>\r\n<p>add_theme_support( \'post-thumbnails\'); <strong>//add in functions.php</strong></p>\r\n<p>the_post_thumbnail(\"thumbnail|midium|large|full\"); <strong>// index.php 130,300,1200 ,oriignal size,</strong></p>\r\n<p><strong>4.Add custom image sizes</strong></p>\r\n<p>add_image_size(\"smallterRizi\",120,110); <strong>//functions.php theme support function, this will make copy of orignal image also 120x110</strong></p>\r\n<p>the_post_thumbnail(\"smallterRizil\"); <strong>// index.php display post feature image</strong></p>\r\n<p><strong>5.Pagination of posts</strong></p>\r\n<p><strong>echo paginate_links(); //index.php</strong></p>', 14, '2020-03-29 16:58:21'),
(202, 'Add header image tab inside theme customizer ||WordPress', '<p><strong>1.//functions.php</strong></p>\r\n<pre class=\"language-markup\"><code>$args = array(\r\n	\'flex-width\'    =&gt; true,\r\n	\'width\'         =&gt; 980,\r\n	\'flex-height\'    =&gt; true,\r\n	\'height\'        =&gt; 200,\r\n	\'default-image\' =&gt; get_template_directory_uri() . \'/images/header.jpg\',\r\n);\r\nadd_theme_support( \'custom-header\', $args );</code></pre>\r\n<p><strong>2.index.php</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;img src=\"&lt;?php header_image(); ?&gt;\" height=\"&lt;?php echo get_custom_header()-&gt;height; ?&gt;\" width=\"&lt;?php echo get_custom_header()-&gt;width; ?&gt;\" alt=\"\" /&gt;</code></pre>\r\n<ul>\r\n<li>header_image(); //display header image</li>\r\n<li>get_custom_header();//display width,height,uri, etc&nbsp;</li>\r\n</ul>', 14, '2020-03-29 17:00:12'),
(203, 'Page Template', '<p>You can create many templates ie <strong>about-page.php, feedback-pageX.php</strong> //but meta data is must&nbsp;</p>\r\n<pre class=\"language-markup\"><code>//contact-page.php\r\n&lt;?php\r\n/*\r\n*Template Name:contactUs\r\n*/\r\n//this will add a new template dropdown in pages\r\n?&gt;\r\n&lt;?php \r\n   $img_uri=get_the_post_thumbnail_url(get_the_ID(),\'full\');\r\n?&gt;\r\n    	&lt;img src=\"&lt;?php echo $img_uri;?&gt;\"&gt;\r\n&lt;!-- this will display feature image of unique page --&gt;</code></pre>', 14, '2020-03-29 17:03:40'),
(204, 'wp_query ||WordPress', '<p>wp_query Used to fetch specific data.</p>\r\n<pre class=\"language-php\"><code>&lt;?php\r\n    	//$query = new WP_Query( array( \'author\' =&gt; 1 ) );\r\n    	//$query = new WP_Query( array( \'cat\' =&gt; 4 ) );\r\n    	$query = new WP_Query( array( \'post_type\' =&gt; \'page\' ) );\r\n		if ( $query-&gt;have_posts() ) :\r\n			while ( $query-&gt;have_posts() ) : $query-&gt;the_post(); ?&gt;\r\n		 		&lt;article class=\"post text-center\"&gt;\r\n			        &lt;h2&gt;&lt;a href=\"&lt;?php the_permalink() ?&gt;\"&gt;&lt;?php the_title(\'&lt;h5&gt;\',\'&lt;/h5&gt;\') ?&gt;&lt;/a&gt;&lt;/h2&gt;\r\n					&lt;?php the_content() ?&gt;\r\n				&lt;/article&gt;\r\n			&lt;?php endwhile;\r\n		else :\r\n			echo \'&lt;p&gt;There are no posts!&lt;/p&gt;\';\r\n		wp_reset_postdata();\r\n		endif;\r\n	?&gt;</code></pre>\r\n<p><a href=\"https://developer.wordpress.org/reference/classes/wp_query/\" target=\"_blank\" rel=\"noopener\">www.developer.wordpress.org/reference/classes/wp_query/</a></p>', 14, '2020-03-29 17:05:27'),
(205, 'Customizer API. Live changes in theme customization ||WordPress', '<pre class=\"language-php\"><code>function mytheme_customize_register( $wp_customize ) {\r\n   //All our panel,sections, settings, and controls will be added here\r\n	$wp_customize-&gt;add_panel( \'panelIdByRizi\', array(\r\n	  \'title\' =&gt; __( \'RIZI GONDAL PANEL\' ),\r\n	  \'description\' =&gt; \'RIZI GONDAL MAKED THIS PANEL\', // Include html tags such as &lt;p&gt;.\r\n	  \'priority\' =&gt; 10, // Mixed with top-level-section hierarchy.\r\n	) );\r\n	###############################section###############################\r\n	$wp_customize-&gt;add_section( \'sectionnIdByRizi\', array( //this is section id,title_tagline,header_image,background_image,nav_menus,widgets,static_front_page,custom_css\r\n	  \'title\' =&gt; __( \'Section By Rizi Gondal\' ),\r\n	  \'description\' =&gt; __( \'RIZI GONDAL MAKE THIS SECTION\' ),\r\n	  \'panel\' =&gt; \'panelIdByRizi\', // Not typically needed.\r\n	  \'priority\' =&gt; 1,\r\n	  \'capability\' =&gt; \'edit_theme_options\',\r\n	  \'theme_supports\' =&gt; \'\', // Rarely needed.\r\n	) );\r\n	###############################Text Color###############################\r\n	$wp_customize-&gt;add_setting( \'RiziTextColor\', array(\r\n	  \'type\' =&gt; \'theme_mod\', // for theme modify or \'option\'(for site)\r\n	  \'capability\' =&gt; \'edit_theme_options\',\r\n	  \'theme_supports\' =&gt; \'\', // Rarely needed.\r\n	  \'default\' =&gt; \'\',\r\n	  \'transport\' =&gt; \'refresh\', // or postMessage\r\n	  \'sanitize_callback\' =&gt; \'sanitize_hex_color\', //postMessage\r\n	  \'sanitize_js_callback\' =&gt; \'\', // Basically to_json. theme_slug_sanitize_radio,theme_slug_sanitize_checkbox,theme_slug_sanitize_select,wp_filter_nohtml_kses(text area),sanitize_email,esc_url_raw,absint,wp_strip_all_tags(css),sanitize_hex_color,\r\n	) );\r\n\r\n	$wp_customize-&gt;add_control( \'RiziTextColor\', array(\r\n	  \'type\' =&gt; \'color\',\r\n	  \'priority\' =&gt; 10, // Within the section.\r\n	  \'section\' =&gt; \'sectionnIdByRizi\', // Required, core or custom.\r\n	  \'label\' =&gt; __( \'Text Color\' ),\r\n	  \'description\' =&gt; __( \'This will change all posts color.\' )\r\n	) );\r\n	###############################Heading Color###############################\r\n	$wp_customize-&gt;add_setting( \'RiziHeadingsColor\', array(\r\n	  \'type\' =&gt; \'theme_mod\', // for theme modify or \'option\'(for site)\r\n	  \'capability\' =&gt; \'edit_theme_options\',\r\n	  \'theme_supports\' =&gt; \'\', // Rarely needed.\r\n	  \'default\' =&gt; \'\',\r\n	  \'transport\' =&gt; \'refresh\', // or postMessage\r\n	  \'sanitize_callback\' =&gt; \'sanitize_hex_color\', //postMessage\r\n	  \'sanitize_js_callback\' =&gt; \'\', // Basically to_json. theme_slug_sanitize_radio,theme_slug_sanitize_checkbox,theme_slug_sanitize_select,wp_filter_nohtml_kses(text area),sanitize_email,esc_url_raw,absint,wp_strip_all_tags(css),sanitize_hex_color,\r\n	) );\r\n\r\n	$wp_customize-&gt;add_control( \'RiziHeadingsColor\', array(\r\n	  \'type\' =&gt; \'color\',\r\n	  \'priority\' =&gt; 10, // Within the section.\r\n	  \'section\' =&gt; \'sectionnIdByRizi\', // Required, core or custom.\r\n	  \'label\' =&gt; __( \'Headings Color\' ),\r\n	  \'description\' =&gt; __( \'This will change all headings color.\' )\r\n	) );\r\n}\r\nadd_action( \'customize_register\', \'mytheme_customize_register\' );</code></pre>\r\n<p>&nbsp;</p>\r\n<pre class=\"language-markup\"><code>body{\r\n	background-color: &lt;?php echo get_theme_mod(\"RiziHeadingColor\",\"#fff\")?&gt;;\r\n} </code></pre>', 14, '2020-03-29 17:10:40'),
(206, 'custom post types define ||WordPress', '<p><strong>//Functions.php</strong></p>\r\n<pre class=\"language-php\"><code>&lt;?php\r\nfunction create_custompost_cpt() {\r\n	$labels = array(\r\n		\'name\' =&gt; _x( \'Youtube Data\', \'Post Type General Name\', \'textdomain\' ),\r\n		\'singular_name\' =&gt; _x( \'Youtube post\', \'Post Type Singular Name\', \'textdomain\' ),\r\n		\'menu_name\' =&gt; _x( \'Youtube posts\', \'Admin Menu text\', \'textdomain\' ),\r\n		\'name_admin_bar\' =&gt; _x( \'Youtube post\', \'Add New on Toolbar\', \'textdomain\' ),\r\n		\'archives\' =&gt; __( \'Custom Post Archives\', \'textdomain\' ),\r\n		\'attributes\' =&gt; __( \'Custom Post Attributes\', \'textdomain\' ),\r\n		\'parent_item_colon\' =&gt; __( \'Parent Custom Post:\', \'textdomain\' ),\r\n		\'all_items\' =&gt; __( \'All Custom Posts\', \'textdomain\' ),\r\n		\'add_new_item\' =&gt; __( \'Add New Custom Post\', \'textdomain\' ),\r\n		\'add_new\' =&gt; __( \'Add New\', \'textdomain\' ),\r\n		\'new_item\' =&gt; __( \'New Custom Post\', \'textdomain\' ),\r\n		\'edit_item\' =&gt; __( \'Edit Custom Post\', \'textdomain\' ),\r\n		\'update_item\' =&gt; __( \'Update Custom Post\', \'textdomain\' ),\r\n		\'view_item\' =&gt; __( \'View Custom Post\', \'textdomain\' ),\r\n		\'view_items\' =&gt; __( \'View Custom Posts\', \'textdomain\' ),\r\n		\'search_items\' =&gt; __( \'Search Custom Post\', \'textdomain\' ),\r\n		\'not_found\' =&gt; __( \'Not found\', \'textdomain\' ),\r\n		\'not_found_in_trash\' =&gt; __( \'Not found in Trash\', \'textdomain\' ),\r\n		\'featured_image\' =&gt; __( \'Featured Image\', \'textdomain\' ),\r\n		\'set_featured_image\' =&gt; __( \'Set featured image\', \'textdomain\' ),\r\n		\'remove_featured_image\' =&gt; __( \'Remove featured image\', \'textdomain\' ),\r\n		\'use_featured_image\' =&gt; __( \'Use as featured image\', \'textdomain\' ),\r\n		\'insert_into_item\' =&gt; __( \'Insert into Custom Post\', \'textdomain\' ),\r\n		\'uploaded_to_this_item\' =&gt; __( \'Uploaded to this Custom Post\', \'textdomain\' ),\r\n		\'items_list\' =&gt; __( \'Custom Posts list\', \'textdomain\' ),\r\n		\'items_list_navigation\' =&gt; __( \'Custom Posts list navigation\', \'textdomain\' ),\r\n		\'filter_items_list\' =&gt; __( \'Filter Custom Posts list\', \'textdomain\' ),\r\n	);\r\n	$args = array(\r\n		\'label\' =&gt; __( \'Custom Post\', \'textdomain\' ),\r\n		\'description\' =&gt; __( \'\', \'textdomain\' ),\r\n		\'labels\' =&gt; $labels,\r\n		\'menu_icon\' =&gt; \'dashicons-video-alt3\',\r\n		\'supports\' =&gt; array(),\r\n		\'taxonomies\' =&gt; array(\'category\',\'post_tag\'),\r\n		\'public\' =&gt; true,\r\n		\'show_ui\' =&gt; true,\r\n		\'show_in_menu\' =&gt; true,\r\n		\'menu_position\' =&gt; 5,\r\n		\'show_in_admin_bar\' =&gt; true,\r\n		\'show_in_nav_menus\' =&gt; true,\r\n		\'can_export\' =&gt; true,\r\n		\'has_archive\' =&gt; true,\r\n		\'hierarchical\' =&gt; false,\r\n		\'exclude_from_search\' =&gt; false,\r\n		\'show_in_rest\' =&gt; true,\r\n		\'publicly_queryable\' =&gt; true,\r\n		\'capability_type\' =&gt; \'post\',\r\n	);\r\n	register_post_type( \'custompost\', $args );\r\n}\r\nadd_action( \'init\', \'create_custompost_cpt\', 0 );\r\n</code></pre>\r\n<p><strong>//index.php</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;div class=\"youtube-posts\"&gt;\r\n	    	&lt;?php\r\n	    	$query = new WP_Query( array( \'post_type\' =&gt; \'custompost\' ) );\r\n			if ( $query-&gt;have_posts() ) :\r\n				while ( $query-&gt;have_posts() ) : $query-&gt;the_post(); ?&gt;\r\n\r\n			 		&lt;article class=\"post text-center\"&gt;\r\n				        &lt;h2&gt;&lt;a href=\"&lt;?php the_permalink() ?&gt;\"&gt;&lt;?php the_title(\'&lt;h5&gt;\',\'&lt;/h5&gt;\') ?&gt;&lt;/a&gt;&lt;/h2&gt;\r\n						&lt;?php the_content() ?&gt;\r\n					&lt;/article&gt;\r\n				&lt;?php endwhile;\r\n			else :\r\n				echo \'&lt;p&gt;There are no posts!&lt;/p&gt;\';\r\n			wp_reset_postdata();\r\n			endif;\r\n		?&gt;\r\n	&lt;/div&gt;</code></pre>\r\n<p>&nbsp;</p>\r\n<