(212, 'Load file data using ajax ||JQuery', '<p>Load data from file and return to the element</p>\r\n<pre class=\"language-javascript\"><code>&lt;script&gt;\r\n$(document).ready(function(){\r\n  $(\"button\").click(function(){\r\n	  $(\"#div1\").load(\"demo_test.txt\", function(responseTxt, statusTxt, xhr){\r\n	    if(statusTxt == \"success\")\r\n	      alert(\"External content loaded successfully!\");\r\n	    if(statusTxt == \"error\")\r\n	      alert(\"Error: \" + xhr.status + \": \" + xhr.statusText);\r\n	  });\r\n	});\r\n});\r\n&lt;/script&gt;</code></pre>', 2, '2020-03-31 15:09:25'),
(213, 'Run php files usign Cronjobs on localhost using task schduler ||PhP', '<p>set script.bat paths..</p>\r\n<p>set script path in shellscript.vbs</p>\r\n<ol>\r\n<li>Open Task Scheduler using run command (taskschd.msc)</li>\r\n<li>Go to &gt;Action menu&gt;Create Task</li>\r\n<li>Fill the Name and Description</li>\r\n<li>Triggers tab, hit New button &gt;schedule and choose Daily</li>\r\n<li>Advanced settings</li>\r\n<li>select Repeat task every (time) &amp;&amp; Indefinitely.</li>\r\n<li>Actions tab &gt;select Start a program.</li>\r\n<li>and give path of your <strong>.vbs file</strong></li>\r\n</ol>\r\n<p><strong>ShellScript.vbs</strong></p>\r\n<pre class=\"language-markup\"><code>Set WinScriptHost = CreateObject(\"WScript.Shell\")\r\nWinScriptHost.Run Chr(34) &amp; \"C:\\xampp\\htdocs\\cronJob\\script.bat\" &amp; Chr(34), 0\r\nSet WinScriptHost = Nothing</code></pre>\r\n<p><strong>Script.bat</strong></p>\r\n<pre class=\"language-markup\"><code>\"C:\\xampp\\php\\php.exe\" -f \"C:\\xampp\\htdocs\\cronJob\\index.php\"</code></pre>\r\n<p><strong>index.php</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;?php\r\n   file_put_contents(\'./rizi\',date(\'h:i:s\').\"\\n\",FILE_APPEND);\r\n?&gt;</code></pre>', 1, '2020-03-31 15:17:37'),
(214, 'CURL: open simple url and print url data ||php', '<pre class=\"language-markup\"><code>&lt;?php\r\n	$ch= curl_init();\r\n	curl_setopt($ch,CURLOPT_URL,\"https://www.google.com/search?q=test\");\r\n	curl_setopt($ch,CURLOPT_FOLLOWLOCATION,1); //if https://www.google.com/search?q=test want to redirect some other like google.in/?etc\r\n	curl_setopt($ch,CURLOPT_RETURNTRANSFER,1); \r\n	$response=curl_exec($ch);\r\n	curl_close($ch);\r\n	 echo $response;\r\n?&gt;</code></pre>', 1, '2020-03-31 15:55:44'),
(215, 'CURL: open a url and find all anchors ||PHP', '<pre class=\"language-markup\"><code>include \'simple_html_dom.php\';\r\n$ch = curl_init();\r\ncurl_setopt($ch, CURLOPT_URL, \"https://www.google.com/search?q=test\");\r\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\n$result = curl_exec($ch);\r\nif(curl_errno($ch))\r\n    echo \'Curl error: \'.curl_error($ch);\r\ncurl_close ($ch);\r\n$html = new simple_html_dom();\r\n$html-&gt;load($result);\r\nforeach($html-&gt;find(\'a\') as $link){\r\n	echo $link-&gt;plaintext.\"&lt;br/&gt;\";\r\n}\r\n</code></pre>\r\n<ul>\r\n<li>get html dom here <a href=\"https://simplehtmldom.sourceforge.io/\">simplehtmldom.sourceforge.io</a></li>\r\n</ul>', 1, '2020-03-31 16:07:53'),
(216, 'CURL: Scrape dynamic data from ajax file ||PHP', '<pre class=\"language-markup\"><code>include \'simple_html_dom.php\';\r\n	$postFields=array(\r\n		\"getPostsNewDesign\"=&gt;\"1\",\r\n		\"start\"=&gt;\"0\",\r\n		\"limit\"=&gt;\"20\",\r\n		\"q\"=&gt;\"notQ\"\r\n	); //dynamic data , scrape first 20 tutorials\r\n	 $ch = curl_init();\r\ncurl_setopt($ch, CURLOPT_URL, \"http://codingpassiveincome.com/ajax.php\");\r\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\ncurl_setopt($ch, CURLOPT_POST,1);\r\ncurl_setopt($ch, CURLOPT_POSTFIELDS,http_build_query($postFields));\r\n$result = json_decode(curl_exec($ch));\r\nif(curl_errno($ch))\r\n    echo \'Curl error: \'.curl_error($ch);\r\ncurl_close ($ch);\r\n$html = new simple_html_dom();\r\n$html-&gt;load($result-&gt;recent);\r\nforeach($html-&gt;find(\'h2[class=box__title]\') as $link){\r\n	echo $link-&gt;plaintext.\"&lt;br/&gt;\";\r\n}</code></pre>', 1, '2020-03-31 16:11:05'),
(217, 'CURL: Login to facebook ||PHP', '<ul>\r\n<li>CURLOPT_SSL_VERIFYPEER <strong>verify ssl certificate, if fails no connection maked</strong></li>\r\n<li>CURLOPT_SSL_VERIFYHOST <strong>1 to check common name,2 check name and verify it matches the hostname provided. 0 to not check the names in</strong></li>\r\n<li>&nbsp;CURLOPT_COOKIEJAR <strong>Save all internal cookies to when the handle is closed</strong></li>\r\n<li>CURLOPT_COOKIEFILE <strong>name of the file containing the cookie data</strong></li>\r\n<li>CURLOPT_USERAGENT <strong>User-Agent in http network tab</strong></li>\r\n</ul>\r\n<p>&nbsp;</p>\r\n<pre class=\"language-markup\"><code>$login_email = \'USERNAME\';\r\n$login_pass = \'PASS\';\r\n$ch = curl_init();\r\ncurl_setopt($ch, CURLOPT_URL, \'https://www.facebook.com/login.php\');\r\ncurl_setopt($ch, CURLOPT_POSTFIELDS,\'email=\'.urlencode($login_email).\'&amp;pass=\'.urlencode($login_pass).\'&amp;login=Login\');\r\ncurl_setopt($ch, CURLOPT_POST, 1);\r\ncurl_setopt($ch, CURLOPT_HEADER, 0);\r\ncurl_setopt($ch, CURLOPT_FOLLOWLOCATION, 2);\r\ncurl_setopt($ch, CURLOPT_COOKIEJAR, \"cookies.txt\");\r\ncurl_setopt($ch, CURLOPT_COOKIEFILE, \"cookies.txt\");\r\ncurl_setopt($ch, CURLOPT_COOKIESESSION, false);\r\ncurl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\ncurl_setopt($ch, CURLOPT_USERAGENT, \"Mozilla/5.0 (Windows NT 10.0; Win64; x64) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/77.0.3865.90 Safari/537.36\");\r\ncurl_setopt($ch, CURLOPT_REFERER, \"http://www.facebook.com\");\r\n$page = curl_exec($ch) or die(curl_error($ch));\r\necho $page;</code></pre>', 1, '2020-03-31 17:37:02'),
(218, 'CURL: Open multiple web pages after login ||php', '<pre class=\"language-markup\"><code>$postFields=array(\r\n	\"user\"=&gt;\"user123\",\r\n	\"pass\"=&gt;\"12345678\",\r\n	\"submit\"=&gt;\"\"\r\n	);\r\n	$ch = curl_init();\r\n	curl_setopt($ch, CURLOPT_URL,\"http://testweb.com/login.php\");\r\n	curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\n	curl_setopt($ch, CURLOPT_POST,1);\r\n	//curl_setopt($ch, CURLOPT_SSL_VERIFYPEER, 0); //if web is secure\r\n	curl_setopt($ch, CURLOPT_POSTFIELDS,http_build_query($postFields));\r\n	curl_setopt($ch, CURLOPT_COOKIEJAR, \"cookie.txt\");\r\n	$result = curl_exec($ch);\r\n		if(curl_errno($ch)){\r\n			echo \'Curl error: \'.curl_error($ch);\r\n		}\r\n	//login succes now we access other pages\r\n	for($i=1;$i&lt;=40;$i++){\r\n		curl_setopt($ch, CURLOPT_URL,\"http://testweb.com/$i.php\");\r\n		curl_setopt($ch, CURLOPT_POST,1);\r\n		curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);\r\n		curl_setopt($ch, CURLOPT_COOKIEJAR, \"cookie.txt\");\r\n		$result2 = curl_exec($ch);\r\n		//echo $result2;\r\n		}\r\n		curl_close($ch);</code></pre>', 1, '2020-03-31 17:40:41'),
(219, 'array_push in PHP', '<pre class=\"language-markup\"><code>&lt;?php\r\n$a=array(\"red\",\"green\");\r\narray_push($a,\"blue\",\"yellow\");\r\nprint_r($a);\r\n?&gt;</code></pre>', 1, '2020-04-01 11:32:06'),
(220, 'Space between table rows |Margin , padding between table rows ||css', '<p>1.We can use this</p>\r\n<pre class=\"language-markup\"><code>table {\r\n  border-collapse:separate; \r\n  border-spacing: 0 1em;\r\n}</code></pre>\r\n<p>2.OR we can use this</p>\r\n<pre class=\"language-markup\"><code>tr\r\n{\r\n    line-height:30px;\r\n}</code></pre>\r\n<p>&nbsp;</p>\r\n<p>3.OR&nbsp;</p>\r\n<pre class=\"language-markup\"><code>&lt;tr style=\"height: 20px;\"&gt; &lt;!-- Mimic the margin --&gt;\r\n&lt;/tr&gt;</code></pre>', 5, '2020-04-02 18:57:46'),
