(248, 'copy data to clipboard |javascript', '<p><strong>index.html</strong></p>\r\n<pre class=\"language-markup\"><code>&lt;div id=\"select_txt\"&gt;This will be copied to clipboard!&lt;/div&gt;\r\n&lt;button type=\"button\" onclick=\"copy_data(select_txt)\"&gt;copy&lt;/button&gt;\r\n\r\n&lt;script type=\"text/javascript\"&gt;\r\n	\r\n	function copy_data(containerid) {\r\n	  var range = document.createRange();\r\n	  range.selectNode(containerid); //changed here\r\n	  window.getSelection().removeAllRanges(); \r\n	  window.getSelection().addRange(range); \r\n	  document.execCommand(\"copy\");\r\n	  window.getSelection().removeAllRanges();\r\n	  alert(\"data copied\");\r\n	}	\r\n&lt;/script&gt;	</code></pre>', 11, '2020-04-28 23:01:25'),
